function lazyloadImages() {
    const images = document.querySelectorAll('img[data-lazy-src]');

    const loadImages = (entries, observer) => {
        entries.forEach(entry => {
            if (entry.isIntersecting) {
                const img = entry.target;
                img.src = img.dataset.src;
                img.classList.remove('lazy');
                observer.unobserve(img);
            }
        });
    };

    const options = {
        root: document.querySelector('body'), // You can change this to the element you want to observe (optional)
        rootMargin: '200px 0px', // Change this to adjust the loading threshold (optional)
        threshold: 0.5
    };

    const observer = new IntersectionObserver(loadImages, options);

    images.forEach(image => {
        observer.observe(image);
        image.classList.add('lazy'); // Add a CSS class for styling (optional)
    });
}

// Call the function after the DOM is loaded
window.addEventListener('DOMContentLoaded', lazyloadImages);
